import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';

interface Pokemon {
  id: number;
  name: string;
  height: number;
  weight: number;
  types: Array<{
    type: {
      name: string;
    };
  }>;
  sprites: {
    front_default: string;
  };
  stats: Array<{
    base_stat: number;
    stat: {
      name: string;
    };
  }>;
}

const PokemonDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [pokemon, setPokemon] = useState<Pokemon | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [allPokemon, setAllPokemon] = useState<Array<{ name: string; url: string }>>([]);

  // Fetch all Pokémon names for navigation
  useEffect(() => {
    const fetchPokemonNames = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=1000');
        const data = await response.json();
        setAllPokemon(data.results);
      } catch (err) {
        console.error('Failed to fetch Pokémon names:', err);
      }
    };
    fetchPokemonNames();
  }, []);

  // Fetch Pokémon details
  useEffect(() => {
    const fetchPokemon = async () => {
      if (!id) return;

      setLoading(true);
      setError(null);

      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`);
        if (!response.ok) {
          throw new Error('Pokémon not found');
        }
        const data = await response.json();
        setPokemon(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'An error occurred');
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, [id]);

  const getStatValue = (statName: string): number => {
    if (!pokemon) return 0;
    const stat = pokemon.stats.find(s => s.stat.name === statName);
    return stat ? stat.base_stat : 0;
  };

  const getCurrentIndex = (): number => {
    if (!pokemon) return -1;
    return allPokemon.findIndex(p => p.name === pokemon.name);
  };

  const navigateToPokemon = (direction: 'prev' | 'next') => {
    const currentIndex = getCurrentIndex();
    if (currentIndex === -1) return;

    let newIndex: number;
    if (direction === 'prev') {
      newIndex = currentIndex > 0 ? currentIndex - 1 : allPokemon.length - 1;
    } else {
      newIndex = currentIndex < allPokemon.length - 1 ? currentIndex + 1 : 0;
    }

    const pokemonId = allPokemon[newIndex].url.split('/').slice(-2, -1)[0];
    navigate(`/pokemon/${pokemonId}`);
  };

  if (loading) {
    return (
      <div className="detail-view">
        <div className="button-container">
          <Link to="/" className="nav-button">
            ← Back to Search
          </Link>
        </div>
        <div className="detail-container">
          <div className="loading">Loading Pokémon details...</div>
        </div>
      </div>
    );
  }

  if (error || !pokemon) {
    return (
      <div className="detail-view">
        <div className="button-container">
          <Link to="/" className="nav-button">
            ← Back to Search
          </Link>
        </div>
        <div className="detail-container">
          <div className="error-message">
            {error || 'Pokémon not found'}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="detail-view">
      <div className="button-container">
        <Link to="/" className="nav-button">
          ← Back to Search
        </Link>
      </div>

      <div className="detail-container">
        <div className="pokemon-detail-card">
          <div className="detail-header">
            <h1 className="pokemon-detail-name">
              {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
            </h1>
            <div className="pokemon-id">#{pokemon.id}</div>
          </div>

          <div className="detail-content">
            <div className="pokemon-image-section">
              <img 
                src={pokemon.sprites.front_default} 
                alt={pokemon.name}
                className="pokemon-detail-sprite"
              />
            </div>

            <div className="pokemon-info-section">
              <div className="info-section">
                <h3>Basic Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Height:</span>
                    <span className="info-value">{pokemon.height / 10}m</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Weight:</span>
                    <span className="info-value">{pokemon.weight / 10}kg</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Types:</span>
                    <div className="type-badges">
                      {pokemon.types.map(type => (
                        <span key={type.type.name} className="type-badge">
                          {type.type.name.charAt(0).toUpperCase() + type.type.name.slice(1)}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              <div className="stats-section">
                <h3>Base Stats</h3>
                <div className="stats-grid">
                  {pokemon.stats.map(stat => (
                    <div key={stat.stat.name} className="stat-item">
                      <span className="stat-name">
                        {stat.stat.name.charAt(0).toUpperCase() + stat.stat.name.slice(1).replace('-', ' ')}
                      </span>
                      <div className="stat-bar-container">
                        <div 
                          className="stat-bar" 
                          style={{ width: `${(stat.base_stat / 255) * 100}%` }}
                        ></div>
                      </div>
                      <span className="stat-value">{stat.base_stat}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          <div className="navigation-controls">
            <button 
              className="nav-arrow prev-arrow" 
              onClick={() => navigateToPokemon('prev')}
              title="Previous Pokémon"
            >
              ←
            </button>
            <span className="pokemon-counter">
              {getCurrentIndex() + 1} of {allPokemon.length}
            </span>
            <button 
              className="nav-arrow next-arrow" 
              onClick={() => navigateToPokemon('next')}
              title="Next Pokémon"
            >
              →
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PokemonDetail;
